validations.script:
import org.iplass.mtp.*;
import org.iplass.mtp.auth.policy.definition.*;

if (value != null) {
    AuthenticationPolicyDefinition apd = ManagerLocator.instance.getManager(AuthenticationPolicyDefinitionManager.class).get(value);
    if (apd == null) {
        return false;
    }
}
return true;
