buttons.customDisplayTypeScript:
if (!org.iplass.mtp.view.generic.OutputType.EDIT.equals(outputType)) {
	return false;
}

if (entity == null || entity.getOid() == null || entity.getOid().isEmpty()) {
	return false;
}

def aps = org.iplass.mtp.spi.ServiceRegistry.getRegistry().getService(org.iplass.mtp.impl.auth.authenticate.builtin.policy.AuthenticationPolicyService.class);
def policy = aps.getOrDefault(entity.accountPolicy);
if (policy.isResetPasswordWithSpecificPassword()){
    return false;
}

def am = org.iplass.mtp.ManagerLocator.getInstance().getManager(org.iplass.mtp.auth.AuthManager.class);
def auth = org.iplass.mtp.auth.AuthContext.getCurrentContext();
def tenant = auth.tenant;

if (am.canResetCredential(entity.accountPolicy)) {
	if (user.admin) {
		return true;
	} else {
		// ユーザー管理が可能なロールを持っているか
	    if (tenant.tenantAuthInfo.userAdminRoles != null) {
	        for (def role: tenant.tenantAuthInfo.userAdminRoles) {
	            if (auth.userInRole(role)) {
	                return true;
	            }
	        }
	    }
	}
}
return false;

if (!org.iplass.mtp.view.generic.OutputType.EDIT.equals(outputType)) {
	return false;
}

if (entity == null || entity.getOid() == null || entity.getOid().isEmpty()) {
	return false;
}

def aps = org.iplass.mtp.spi.ServiceRegistry.getRegistry().getService(org.iplass.mtp.impl.auth.authenticate.builtin.policy.AuthenticationPolicyService.class);
