return false;

views.javaScript:
function resetPassword() {
	if(window.confirm(scriptContext.locale.passResetConfirm)) {
		if (typeof dType === "undefined") dType = "";
		var action = "/auth/password/reset";
		if ($("body").is(".modal-body")) {
			action = "/auth/password/ref/reset";
			$("<input />").attr({type:"hidden", name:"refEdit", value:true}).appendTo($("#detailForm"));
		}
		$("#detailForm").attr("action", contextPath + dType + action).submit();
	}
}

function resetSpecificPassword(){
    var isSubModal = $("body.modal-body").length !== 0;
    var target = getModalTarget(isSubModal);
    var action = '/gem/auth/specificpassword';
    var oid = $(":hidden[name='oid']").val();
    var execType = $(":hidden[name='execType']").val();
    var $form = $("<form />").attr({method:"POST", action: contextPath + action, target:target}).appendTo("body");
	$("<input />").attr({type:"hidden", name:"oid", value:oid}).appendTo($form);
	$("<input />").attr({type:"hidden", name:"execType", value:execType}).appendTo($form);
	if (isSubModal) $("<input />").attr({type:"hidden", name:"modalTarget", value:target}).appendTo($form);
	$form.submit();
	$form.remove();
}

